          <!-- 用户聊天内容展示区 -->
          <div class="chat-message" id="chat-message-id">
            <div
              class="chat-message-body"
              v-for="(item, index) in messageArr"
              :key="index"
            >
              {{ item }}
            </div>
          </div>
          <!-- 聊天输入框和发送按钮 -->
          <div class="sendContent">
            <!-- 发送附件的图标 -->
            <paper-clip-outlined style="font-size: large; margin-right: 10px" />
            <!-- 输入框和发送按钮表单 -->
            <a-form
              layout="inline"
              @finish="onFinish"
              :model="formState"
              ref="formRef"
              autoComplete="off"
            >
              <!-- 聊天输入框 -->
              <a-form-item name="messageContent">
                <a-input
                  placeholder="Write a message ..."
                  v-model:value="formState.messageContent"
                />
              </a-form-item>
              <!-- 发送按钮 -->
              <a-form-item style="margin-right: 0px">
                <a-button
                  type="primary"
                  html-type="submit"
                  :disabled="disabled"
                >
                  Send
                </a-button>
              </a-form-item>
            </a-form>
          </div>
        </div>
      </div>
    </div>
  </div>
</template>

<script lang="ts">
import {
  ref,
  reactive,
  defineComponent,
  computed,
  onUpdated,
  nextTick,
} from "vue";
import { IMClient } from "yzim";
import { hostAddress } from "../common/hostConfig";
import { SearchOutlined, PaperClipOutlined } from "@ant-design/icons-vue";
import type { FormInstance } from "ant-design-vue";
import { IFormState } from "../interfaces/index";

export default defineComponent({
  components: {
    SearchOutlined,
    PaperClipOutlined,
  },
  name: "Dashboard",
  setup() {
    // 定义表单的ref标识
    const formRef = ref<FormInstance>();
    // 定义聊天输入框内容
    const formState = reactive<IFormState>({
      messageContent: "",
    });
    // 定义收集发送信息的数组
    const messageArr = ref<string[]>([]);
    // 在页面渲染前创建服务器连接示例
    let cli = new IMClient(hostAddress, "terry");
    // 登陆服务器
    cli.login();
    // 输入框的发送信息事件
    const onFinish = (formState: IFormState) => {
      // 将信息发送内容放到数组中
      messageArr.value.push(formState.messageContent);
      // 向服务器发送信息内容
      cli.sendText(formState.messageContent);
      // 重置聊天输入框
      formRef.value?.resetFields();
    };

    // 计算输入框中的值
    const disabled = computed(() => {
      return !formState.messageContent;
    });

    //页面更新后的生命周期
    onUpdated(() => {
      // 页面更新后, 重新计算显示内容区域滚动条的位置, 使滚动条在每次页面更新后都处于最底部
      nextTick(() => {
        // 通过id获取到信息内容展示框
        let div = document.getElementById("chat-message-id");
        // 将添加新内容后的内容展示框高度重新赋值给垂直滚动条.
        div!.scrollTop = div!.scrollHeight;
      });
    });

    return {
      onFinish,
      formState,
      disabled,
      messageArr,
      formRef,
    };
  },
});
</script>

